// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IUMAOptimisticOracle {
    function proposePrice(address requester, bytes32 identifier, uint256 timestamp, bytes calldata ancillaryData) external;
    function getPrice(address requester, bytes32 identifier, uint256 timestamp) external view returns (bool isValid, uint256 price, uint256 returnedTimestamp);
}

contract PredictionMarket {
    enum Stage { Commit, Reveal, Propose, Dispute, Resolved }
    Stage public currentStage;

    string public eventDescription;
    uint8 public winningOutcome;
    address public marketCreator;

    uint256 public totalPool0; 
    uint256 public totalPool1;

    struct Bet {
        bytes32 commitHash;
        uint256 amount;
        bool revealed;
        uint8 choice;
    }

    mapping(address => Bet) public userBets;
    IUMAOptimisticOracle public umaOracle;
    bytes32 public identifier;
    uint256 public proposalTime;
    uint256 public disputePeriod;

    constructor(address _umaOracle, string memory _description, uint256 _disputePeriod) {
        umaOracle = IUMAOptimisticOracle(_umaOracle);
        eventDescription = _description;
        disputePeriod = _disputePeriod;
        marketCreator = msg.sender;
        currentStage = Stage.Commit;
        identifier = keccak256(abi.encodePacked(block.timestamp, _description));
    }

    // --- FONCTION POUR LE FRONTEND ---
   function getMarketData(address user) external view returns (
        string memory desc,
        uint256 p0,
        uint256 p1,
        uint8 stage,
        uint256 userTotalStake
    ) {
        desc = eventDescription;
        p0 = totalPool0;
        p1 = totalPool1;
        stage = uint8(currentStage);
        userTotalStake = userBets[user].amount;
    }


    function commitBet(bytes32 _hash) external payable {
        require(currentStage == Stage.Commit, "Phase incorrecte");
        require(msg.value > 0, "Mise vide");
        
        Bet storage b = userBets[msg.sender];
        b.commitHash = _hash; // Note: Le dernier hash écrase le précédent (standard commit/reveal)
        b.amount += msg.value; // On CUMULE la valeur envoyée
    }

    function startRevealPhase() external { currentStage = Stage.Reveal; }

    function revealBet(uint8 _choice, string memory _salt) external {
        require(currentStage == Stage.Reveal, "Phase incorrecte");
        Bet storage b = userBets[msg.sender];
        require(keccak256(abi.encodePacked(_choice, _salt)) == b.commitHash, "Hash incorrect");
        b.choice = _choice;
        b.revealed = true;
        if (_choice == 1) totalPool1 += b.amount; else totalPool0 += b.amount;
    }
}
